<fieldset><legend><?=$vendor->vendorName; ?></legend><div id="vendor_buttons" class="button-row"> <?   print "<span class='button edit_vendor' id='edit_$vendor->kVendor'>Edit</span>&nbsp;&nbsp;";    print "<span class='button view_orders' id='list_$vendor->kVendor'>Orders</span>&nbsp;&nbsp;";print "<span class='new_order new button' id='new_$vendor->kVendor'>New Order</span>";?></div><div id="vendor_view"><p><? if(!empty($vendor->vendorContact)){print "$vendor->vendorContact<br/>";}?><? print "$vendor->vendorAddress<br/>$vendor->vendorCityStateZip"; ?><br/><? print "<a href='$vendor->vendorURL' target='_blank'>$vendor->vendorURL</a>";?><br/><? if(!empty($vendor->vendorPhone)){ print "Phone: $vendor->vendorPhone ";}if(!empty($vendor->vendorFax)){ print "Fax: $vendor->vendorFax";}if(!empty($vendor->vendorCustomerID)){ print "<br/>Customer ID: $vendor->vendorCustomerID";} ?></p></div></fieldset>